	$ConnectBTN_Click={
		if (!(get-pssnapin -name VMware.VimAutomation.Core -erroraction silentlycontinue)) {
			add-pssnapin VMware.VimAutomation.Core 
		}
		$PowerCLIVersion = Get-PowerCLIVersion	
		If ((($PowerCLIVersion).Major -ge 4 -and ($PowerCLIVersion).Minor -ge 1) -or ($PowerCLIVersion).Major -gt 4) { 
		$ProgressTXT.Text = "Connecting to $($vCenterTXT.Text)... Please Wait"
		$ProgressTXT.Update()
		$VIServer = connect-viserver $vCenterTXT.Text -User $UserName.Text -Password $Password.Text
		If ($VIServer.IsConnected -ne $true){
			$ProgressTXT.Text = "Unable to connect - please check connection details & try again"
		} Else {
			$ProgressTXT.Text = "Connected to $($vCenterTXT.Text)"
			$ProgressTXT.Update()
			$ConnectBTN.Enabled = $false
			$vCenterTXT.Enabled = $false
			
			New-VIProperty -Name NumberOfHosts -ObjectType Cluster `
				-Value {
					param($cluster)
	
					@($cluster.Extensiondata.Host).Count
				} `
				-BasedOnExtensionProperty 'Host' `
				-Force
	
			New-VIProperty -Name NumberOfVMs -ObjectType Cluster `
				-Value {
					param($cluster)
	
					($cluster.Extensiondata.Host | %{Get-View $_} | `
					Measure-Object -InputObject {$_.Vm.Count} -Sum).Sum
				} `
				-BasedONextensionProperty 'Host' `
				-Force
	
			New-VIProperty -Name TotalCPUResources -ObjectType Cluster `
				-Value {
					param($cluster)
					"$([Math]::Round($cluster.Extensiondata.Summary.TotalCpu / 1000)) Ghz"
				} -Force
	
			New-VIProperty -Name TotalMEMResources -ObjectType Cluster `
				-Value {
					param($cluster)
					"$([Math]::Round(($cluster.Extensiondata.Summary.TotalMemory / 1GB))) GB"
				} `
				-BasedOnExtensionProperty 'Host' `
				-Force
	
			New-VIProperty -Name NumvMotions -ObjectType Cluster `
				-Value {
					param($cluster)
					$Cluster.ExtensionData.Summary.NumVmotions
				} `
				-BasedOnExtensionProperty 'Host' `
				-Force
				
			New-VIProperty -Name AdmissionControl -ObjectType Cluster `
				-Value {
					param($cluster)
					if ($Cluster.ExtensionData.Configuration.DasConfig.AdmissionControlEnabled) {
						'Do not allow constraint violations'
					} else {
						'Allow constraint violations'
					}
				} `
				-BasedOnExtensionProperty 'Host' `
				-Force
				
			New-VIProperty -Name CurrentFailoverCapacity -ObjectType Cluster `
				-Value {
					param($cluster)
					"$($Cluster.ExtensionData.Summary.AdmissionControlInfo.CurrentFailoverLevel) Hosts"
				} `
				-BasedOnExtensionProperty 'Host' `
				-Force
				
			New-VIProperty -Name ConfiguredFailoverCapacity -ObjectType Cluster `
				-Value {
					param($cluster)
					"$($Cluster.ExtensionData.Configuration.DasConfig.AdmissionControlPolicy.FailoverLevel) Hosts"
				} `
				-BasedOnExtensionProperty 'Host' `
				-Force
	
			New-VIProperty -Name MigrationAutomationLevel -ObjectType Cluster `
				-Value {
					param($cluster)
					switch ($cluster.ExtensionData.Configuration.DrsConfig.DefaultVmBehavior) {
						'fullyAutomated' { 'Fully automated'; break} 
						'partiallyAutomated' {'Partially automated'; break} 
						'manual' {'Manual'; break}
					}
				} `
				-BasedOnExtensionProperty 'Host' `
				-Force
	
			New-VIProperty -Name PowerManagement -ObjectType Cluster `
				-Value {
					param($cluster)
					$Cluster.ExtensionData.ConfigurationEx.DpmConfigInfo.Enabled
				} `
				-BasedOnExtensionProperty 'Host' `
				-Force
	
			New-VIProperty -Name DRSRecommendations -ObjectType Cluster `
				-Value {
					param($cluster)
					$cluster.ExtensionData.DrsRecommendation
				} `
				-BasedOnExtensionProperty 'Host' `
				-Force
	
			New-VIProperty -Name MigrationThreshold -ObjectType Cluster `
				-Value {
					param($cluster)
					"Apply recommendations with $($cluster.ExtensionData.Configuration.DrsConfig.VmotionRate) or more stars."
				} `
				-BasedOnExtensionProperty 'Host' `
				-Force	
			
			
				###############################
				# DRS&HAAudit                 # 
				###############################
	
				$Version = "1.1"
				#
				# Changes:
				# Version 1.0 - Initail Script from Pages 0 - 50 of the HA & DRS Book
	
				# Set if you would like to see the helpfull comments about areas of the checks
				$Comments = $true
	
				# Use the following area to define the colours of the report
				$Colour1 = "000000"
				$Colour2 = "FF9F39"
	
				Write-CustomOut "Collecting VM Objects"
				$VM = Get-VM | Sort Name
				Write-CustomOut "Collecting VM Host Objects"
				$VMH = Get-VMHost | Sort Name
				Write-CustomOut "Collecting Cluster Objects"
				$Clusters = Get-Cluster | Sort Name
				$Date = Get-Date
	
				# Check for vSphere
				If ($serviceInstance.Client.ServiceContent.About.Version -ge 4){
					$vSphere = $true
				}
	
				$MyReport = Get-CustomHTML
					$MyReport += Get-CustomHeader0 "<IMG SRC='data:image/png;base64,' ALT='vCenter'> $($VIServer.Name)"
						
						# ---- General Summary Info ----
						Write-CustomOut "..Adding General Summary Info to the report"
						$CommentsSet = $Comments
						$Comments = $false
						$MyReport += Get-CustomHeader "General Details" ""
							$MyReport += Get-HTMLDetail "Number of Clusters:" (@($Clusters).Count)
							$MyReport += Get-HTMLDetail "Number of Hosts:" (@($VMH).Count)
							$MyReport += Get-HTMLDetail "Number of VMs:" (@($VM).Count)
							$MyReport += Get-HTMLDetail "Active VMs:" (@($VM | Where { $_.PowerState -eq "poweredOn" }).Count) 
							$MyReport += Get-HTMLDetail "In-active VMs:" (@($VM | Where { $_.PowerState -eq "poweredOff" }).Count) 
						$Comments = $CommentsSet
						$MyReport += Get-CustomHeaderClose
						
						# ---- General Cluster Information ----
						Write-CustomOut "..Cluster Details"
						$ClusterDetails = $Clusters
						If (($ClusterDetails | Measure-Object).count -gt 0) {
							$MyReport += Get-CustomHeader "<IMG SRC='data:image/png;base64,' ALT='Clusters'> Cluster Details" "General details of the current clusters attached to this vCenter"
								$MyReport += Get-HTMLTable ($ClusterDetails | Select Name, HAEnabled, HAFailoverLevel, DrsEnabled, DrsAutomationLevel)
							$MyReport += Get-CustomHeaderClose
						}
						
						# ---- Advanced Cluster Information ----
						$CommentsSet = $Comments
						$Comments = $false
						$ClusterDetails = $Clusters
						$MyReport += Get-CustomHeader "The following sections will detail all best practices for each cluster" ""
						$Comments = $CommentsSet
						Foreach ($CurCluster in $ClusterDetails){
							$CommentsSet = $Comments
							$Comments = $false
							$ClusterDetails = $Clusters
							$MyReport += Get-CustomHeader "<IMG SRC='data:image/png;base64,' ALT='Clusters'> CLUSTER: $($CurCluster)" ""
							$Comments = $CommentsSet
								
							# ---- General HA and DRS Info ----
							Write-CustomOut "..Adding General HA and DRS Info to the report"
							$CommentsSet = $Comments
							$Comments = $false
							$MyReport += Get-CustomHeader "Summary of Details" ""
								$MyReport += Get-HTMLDetail "DRS Enabled" $CurCluster.DRSEnabled
								$MyReport += Get-HTMLDetail "HA Enabled:" $CurCluster.HAEnabled
								$MyReport += Get-HTMLDetail "Total CPU Resources:" $CurCluster.TotalCPUResources
								$MyReport += Get-HTMLDetail "Total Memory:" $CurCluster.TotalMemResources
								$MyReport += Get-HTMLDetail "Number of Hosts:" $CurCluster.NumberOfHosts
								$MyReport += Get-HTMLDetail "Total Migrations using vMotion:" $CurCluster.NumvMotions
							$Comments = $CommentsSet
							$MyReport += Get-CustomHeaderClose
							
							$clusterDrsConfigSummary = $CurCluster | Select-Object -Property MigrationAutomationLevel, PowerManagement, DRSRecommendations, MigrationThreshold
							
							if ($CurCluster.HAEnabled) {
								# ---- Detailed HA Information ----
								Write-CustomOut "..Adding HA Info to the report"
								$CommentsSet = $Comments
								$Comments = $false
								$MyReport += Get-CustomHeader "vSphere HA Details" ""
									$MyReport += Get-HTMLDetail "Admission Control" $CurCluster.AdmissionControl
									If ($CurCluster.AdmissionControl -eq "Allow constraint violations") {
										$MyReport += Get-HTMLDetail "Current Failover Capacity:" "N/A"
										$MyReport += Get-HTMLDetail "Configured Failover Capacity:" "N/A"
									} Else {
										$MyReport += Get-HTMLDetail "Current Failover Capacity:" $CurCluster.CurrentFailoverCapacity
										$MyReport += Get-HTMLDetail "Configured Failover Capacity:" $CurCluster.ConfiguredFailoverCapacity
									}
								$Comments = $CommentsSet
								$MyReport += Get-CustomHeaderClose
							}
							if ($CurCluster.DRSEnabled) {
								# ---- Detailed DRS Information ----
								Write-CustomOut "..Adding DRS Info to the report"
								$CommentsSet = $Comments
								$Comments = $false
								$MyReport += Get-CustomHeader "vSphere DRS Details" ""
									$MyReport += Get-HTMLDetail "Migration Automation Level" $CurCluster.MigrationAutomationLevel
									$MyReport += Get-HTMLDetail "Power Management Automation Level:" $CurCluster.PowerManagement
									$MyReport += Get-HTMLDetail "Migration Threshold:" $CurCluster.MigrationThreshold
									$MyReport += Get-HTMLDetail "DRS Recommendations:" $CurCluster.DRSRecommendations
								$Comments = $CommentsSet
								$MyReport += Get-CustomHeaderClose
							}	
				
				
							# ---- Host Build Information ----
							Write-CustomOut "..$Cluster Host Build Details"
							$HostDetails = ($VMH | Where { $_.ParentID -eq $CurCluster.Id })
							If (($HostDetails | Measure-Object).count -gt 0) {
								$MyReport += Get-CustomHeader "<IMG SRC='data:image/png;base64,' ALT='Clusters'>Host Build Details" "It is recommended against using a mixed cluster with different build numbers, see page 21"
									$MyReport += Get-HTMLTable ($HostDetails | Select Name, Version, Build)
								$MyReport += Get-CustomHeaderClose
							}
						
							# ---- Cluster Advanced Configuration Information ----
							Write-CustomOut "..$Cluster Advanced Configuration"
							$ClusterAdvanced = ($CurCluster | Get-AdvancedSetting)
							If (($ClusterAdvanced | Measure-Object).count -gt 0) {
								$MyReport += Get-CustomHeader "Cluster Advanced Details" "It is not recommended that these are changed, various settings are mentioned throughout the book"
									$MyReport += Get-HTMLTable ($ClusterAdvanced | Select Type, Name, Value, Description)
								$MyReport += Get-CustomHeaderClose
							}
								
							$MyReport += Get-CustomHeaderClose
						}
						$MyReport += Get-CustomHeaderClose
								
					$MyReport += Get-CustomHeader0Close
				$MyReport += Get-CustomHTMLClose
	
				Write-CustomOut "..Displaying HTML results"
				if (-not (test-path ([system.Environment]::GetFolderPath('MyDocuments') + "\HA and DRS Audit\"))){
					MD ([system.Environment]::GetFolderPath('MyDocuments') + "\HA and DRS Audit\") | Out-Null
				}
				$Filename = $SaveFile = $Location.Text + $VIServer + "HAandDRSAudit" + "_" + $Date.Day + "-" + $Date.Month + "-" + $Date.Year + ".htm"
				$MyReport | out-file -encoding ASCII -filepath $Filename
				Invoke-Item $Filename
		}
		} Else {
			$Version.Visible = $True
			$ConnectBTN.Enabled = $false
		}
		
	}
	
